package variablesAndDatatypes;

public class SecondJavaClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Arithmetic operations
		
		int a = 10;
		int b = 20;
		// +,-,*,/,%(reminder/mode)
		
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);


		

	}

}
